mapper.select:
select

from article
where ARTICLE_TYPE = #{0}
order by PUB_DATE desc
LIMIT #{1},#{2}

select

from article
where TITLE like #{0}
order by PUB_DATE desc
LIMIT #{1},#{2}

select

from article WHERE PUB_DATE >= #{pubDate,jdbcType=TIMESTAMP}

  AND ARTICLE_TYPE = #{articleType,jdbcType=INTEGER}

ORDER BY PUB_DATE DESC

select

from article WHERE SCAN_NUM >= #{scanNum,jdbcType=INTEGER}

  AND ARTICLE_TYPE = #{articleType,jdbcType=INTEGER}

ORDER BY SCAN_NUM DESC

select

from article WHERE PUB_DATE <= #{pubDate,jdbcType=TIMESTAMP}

  AND ARTICLE_TYPE = #{articleType,jdbcType=INTEGER}

ORDER BY PUB_DATE DESC

select

from article WHERE SCAN_NUM <= #{scanNum,jdbcType=TIMESTAMP}

  AND ARTICLE_TYPE = #{articleType,jdbcType=INTEGER}

ORDER BY SCAN_NUM DESC

mapper.update:
update article
set SCAN_NUM = SCAN_NUM + 1
where ID = #{0}

update article
set LIKE_NUM = LIKE_NUM + 1
where ID = #{0}

update article
set IS_LOOK_BACK = #{1}
where ID = #{0}
